c = Centre.find_by_name('BCM')

bcm_es_cell.each do |symbol|
  g = Gene.find_by_marker_symbol(symbol)
  if !g.nil?
    pp = g.phenotyping_productions
    pp.each do |p|
      if p.mi_plan.mutagenesis_via_crispr_cas9 == false && p.tissue_distribution_centres.blank? && p.production_centre_name == 'BCM' && p.status_id = 4  
        tissue_distribution_fixed = PhenotypingProduction::TissueDistributionCentre.new()
        tissue_distribution_fixed.start_date = DateTime.now
        tissue_distribution_fixed.phenotyping_production_id = p.id
        tissue_distribution_fixed.centre_id = c.id
        tissue_distribution_fixed.deposited_material = 'Paraffin-embedded Sections'
        tissue_distribution_fixed.save!
        new_tissue.push(symbol)
      elsif symbol == "Rrm1"
        tissue_distribution_fixed = PhenotypingProduction::TissueDistributionCentre.new()
        tissue_distribution_fixed.start_date = DateTime.now
        tissue_distribution_fixed.phenotyping_production_id = p.id
        tissue_distribution_fixed.centre_id = c.id
        tissue_distribution_fixed.deposited_material = 'Paraffin-embedded Sections'
        tissue_distribution_fixed.save!
        new_tissue.push(symbol)
      else
        others.push(symbol)
      end
    end
  else
    nil_genes.push(symbol)
  end
end

